menu.event:
if(dt < game.maxTickTime()) then local transitionState = element("transitionState"):GetInt()
if(element:GetElement("LoadingIndicator"):GetComponent("Text")("visible"):GetInt() == 1) then local loadingAnimTime = element("loadingAnimTime"):GetFloat() loadingAnimTime = (loadingAnimTime + dt*3) element("loadingAnimTime"):SetFloat(loadingAnimTime) element:DoStoredScript("TickLoading")
if(loadingAnimTime >= 1.0 and element("loadingState"):GetInt() == 1) then element("loadingState"):SetInt(2) element("loadingStateChanged"):SetInt(1) element("loadingAnimTime"):SetFloat(0.0) elseif (loadingAnimTime >= 1.0 and element("loadingState"):GetInt() == 2)then element("loadingState"):SetInt(3) element("loadingStateChanged"):SetInt(1) element("loadingAnimTime"):SetFloat(0.0) elseif (loadingAnimTime >= 1.0 and element("loadingState"):GetInt() == 3)then element("loadingState"):SetInt(1) element("loadingStateChanged"):SetInt(1) element("loadingAnimTime"):SetFloat(0.0) end end
if(transitionState ~= 0) then local transitionTime = element("transitionTime"):GetFloat()
element:DoStoredScript("TickTransition")
if(transitionState == 1) then transitionTime = (transitionTime + dt*3) elseif(transitionState == 2) then transitionTime = (transitionTime - dt*3) end
transitionTime = clamp( transitionTime, 0.0, 1.0 ) element("transitionTime"):SetFloat(transitionTime)
if(transitionTime >= 1.0) then element("transitionState"):SetInt(0) element("transitionTime"):SetFloat(1.0) element:DoStoredScript("TickTransition") elseif(transitionTime <= 0.0) then game.setClipping("Clipping", 0, 0, lua_sys.screenWidth()*lua_sys.deviceScaleX(), lua_sys.screenHeight()*lua_sys.deviceScaleY()) element["NewsEntries"]:DoStoredScript("dismissPlacement") element:root():popPopUp() end end end

swipeComponent.function:
component:GetVar("direction"):SetInt(lua_sys.MenuSwipeComponent_SwipeDirectionVertical) component:GetVar("mode"):SetInt(lua_sys.MenuSwipeComponent_SwipeModeFree) component:GetVar("tSteps"):SetFloat(25.0) component:listenToTouches(element)
if(element:GetElement(element("FirstEntry"):GetString())) then local totalHeight = element("NumEntries"):GetInt() * element:GetElement(element("FirstEntry"):GetString()):absH()
if(totalHeight > element:absH()) then component:setScrollSize(totalHeight - element:absH()) if(game.hudScale() ~= 1) then component:setScrollSize(totalHeight - element:absH() + 20*game.hudScale()) end else component:setScrollSize(0) end end
