menu.event:
element("transitionState"):SetInt(1) element("transitionTime"):SetFloat(0.0) lua_sys.playSoundFx("audio/sfx/menu_slide.wav")
local statsArray = { "DISPLAY_NAME", "TRIBAL_NAME", "COMPOSER_NAME", "ISLAND"} local labels = {"REPORT_USERNAME_LABEL", "REPORT_TRIBENAME_LABEL", "REPORT_SONGNAME_LABEL", "REPORT_ISLAND_LABEL"}
local previous = nil;
for i=1, #statsArray do
if(include("ReportMenu").ShowButton(i)) then local button = menu:addTemplateElement("template_reportButton", statsArray[i], element) button("id"):SetString(labels[i]) button("desc"):SetString(labels[i].."_CONF") button("label"):SetString(labels[i])
if(previous == nil) then button:relativeTo(element:GetElement("Notification")) button:setOrientation(lua_sys.MenuOrientation(0, 10*lua_sys.screenHeight()/320, 0, lua_sys.HCENTER, lua_sys.TOP)) button:setRelativeObjectAnchors(lua_sys.HCENTER, lua_sys.BOTTOM) else button:relativeTo(previous) button:setOrientation(lua_sys.MenuOrientation(0, 30*lua_sys.screenHeight()/320, 0, lua_sys.HCENTER, lua_sys.TOP)) button:setRelativeObjectAnchors(lua_sys.HCENTER, lua_sys.BOTTOM) end
element("LastItem"):SetString(button:name()) previous = button
button:init() button:postInit() end end
